import { useState, createContext, useEffect } from "react";
import Layout from "./components/Layout";
import Search from "./components/Search";
import Filters from "./components/Filters";
import UserCard from "./components/UserCard";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";
import usersState from "./state/usersState";
import axios from "axios";
import useGetUsers from "./hooks/useGetUsers";

export interface AppContextInterface {
  mobileFiltersOpen: boolean;
  setMobileFiltersOpen: React.Dispatch<React.SetStateAction<boolean>>;
}

export const AppContext = createContext<AppContextInterface>({
  mobileFiltersOpen: false,
  setMobileFiltersOpen: () => {},
});

function App() {
  useGetUsers();
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState<boolean>(false);
  const users = useRecoilValue(usersState);
  return (
    <RecoilRoot>
      <AppContext.Provider
        value={{
          mobileFiltersOpen,
          setMobileFiltersOpen,
        }}
      >
        <Layout>
          <div className={"max-w-3xl mx-auto px-6 flex flex-col lg:px-6 mt-10"}>
            {/* Filters */}
            <Filters />
            <main className={`lg:col-span-8 xl:col-span-6 xl:col-start-3`}>
              {/* Search */}
              <Search />
              {/* User Card */}
              {false ? (
                <span className="mt-4 text-sm text-gray-500 dark:text-gray-200 bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg flex flex-1">
                  User not found
                </span>
              ) : (
                <>
                  <UserCard />
                  <UserCard />
                </>
              )}
            </main>
          </div>
        </Layout>
      </AppContext.Provider>
    </RecoilRoot>
  );
}

export default App;
