import axios from "axios";
import { useEffect } from "react";
import { useQuery } from "react-query";
import { useSetRecoilState } from "recoil";
import reposState from "../state/reposState";
import { IRepos } from "../types";

interface UseGetIRepos {
  results?: number;
  nationalities?: string[];
  page?: number;
  org?: string;
}

const useGetRepos = ({
  results = 50,
  nationalities = [],
  page = 1,
  org = "facebook",
}: UseGetIRepos = {}) => {
  const setRepos = useSetRecoilState(reposState);

  const { isLoading, error, data } = useQuery(["reposData"], () =>
    axios
      .get(`https://api.github.com/orgs/${org}/repos?sort`, {
        headers: {
          Authorization: "token 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",
        },
      })
      .then((res) => res.data)
      .catch((err) => err.response)
  );
  console.log(data, error);
  useEffect(() => {
    if (data) {
      if (isLoading) {
        setRepos((repos: IRepos) => {
          return { ...repos, loading: true, error: false };
        });
      } else {
        console.log("error", error);
        if (error) setRepos({ results: data, loading: false, error: error });
        else {
          console.log("entra");
          if (page === 1) {
            setRepos({ results: data, loading: false, error: null });
          } else {
            setRepos((repos: IRepos) => ({
              ...repos,
              results: [...repos.results, ...data],
              loading: false,
              error: null,
            }));
          }
        }
      }
    }
  }, [isLoading, data]);
};

export default useGetRepos;
