import React from "react";

const UserCard = () => {
  const [active, setActive] = useState(false);
  const [height, setHeight] = useState("0px");
  const [rotate, setRotate] = useState("transform duration-700 ease");

  const contentSpace = useRef(null);

  function toggleAccordion() {
    setActive((prevState) => !prevState);
    // @ts-ignore
    setHeight(active ? "0px" : `${contentSpace.current.scrollHeight}px`);
    setRotate(
      active
        ? "transform duration-700 ease"
        : "transform duration-700 ease rotate-180"
    );
  }

  return (
    <div className="mt-4">
      {false ? (
        <span className="text-sm text-gray-500 dark:text-gray-200 bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg flex flex-1">
          User not found
        </span>
      ) : (
        <ul className="space-y-4">
          <li className="">
            <div className="motion-safe:animate-fadeIn bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg">
              <div className="flex items-center justify-between">
                <div className="flex space-x-3 items-center">
                  <div className="flex-shrink-0">
                    {true ? (
                      <img
                        className="h-10 w-10 rounded-full"
                        src={
                          "https://randomuser.me/api/portraits/med/men/75.jpg"
                        }
                        alt=""
                      ></img>
                    ) : (
                      <svg
                        className="bg-gray-100 text-gray-300 rounded-full overflow-hidden h-10 w-10"
                        fill="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path d="M24 20.993V24H0v-2.996A14.977 14.977 0 0112.004 15c4.904 0 9.26 2.354 11.996 5.993zM16.002 8.999a4 4 0 11-8 0 4 4 0 018 0z" />
                      </svg>
                    )}
                  </div>
                  <div className="min-w-0 flex-1">
                    <p className="text-sm font-medium text-gray-900 dark:text-white mb-1">
                      <span className="hover:underline cursor-pointer">
                        Marc Olson
                      </span>
                    </p>
                    <span className="inline-flex items-center text-sm">
                      <span className="font-medium text-orange-500">
                        marc.olson@example.com
                      </span>

                      <span className="sr-only">karmas amount</span>
                    </span>
                  </div>
                </div>

                <button
                  type="button"
                  className="hidden sm:block inline-flex items-center px-4 py-2 shadow-sm text-sm font-medium rounded-3xl text-white bg-gradient-to-r from-orange-500 to-pink-500 hover:to-pink-600"
                >
                  See more
                </button>
              </div>
              <button
                type="button"
                className="block text-center sm:hidden w-full items-center px-4 py-2 mt-4 shadow-sm text-sm font-medium rounded-3xl text-white bg-gradient-to-r from-orange-500 to-pink-500 hover:to-pink-600"
              >
                See more
              </button>
            </div>
          </li>
          <div className="max-h-96">
            <button
              type="button"
              className="motion-safe:animate-grow block text-center sm:hidden w-full items-center px-4 py-2 mt-4 shadow-sm text-sm font-medium rounded-3xl text-white bg-gradient-to-r from-orange-500 to-pink-500 hover:to-pink-600"
            >
              See more
            </button>
          </div>
        </ul>
      )}
    </div>
  );
};

export default UserCard;
