import {
  useState,
  createContext,
  useEffect,
  ChangeEventHandler,
  FormEventHandler,
} from "react";
import Layout from "./components/Layout";
import Search from "./components/Search";
import Filters from "./components/Filters";
import UserCard from "./components/UserCard";
import {
  RecoilRoot,
  atom,
  selector,
  useRecoilState,
  useRecoilValue,
  useSetRecoilState,
} from "recoil";
import usersState from "./state/usersState";
import axios from "axios";
import useGetUsers from "./hooks/useGetUsers";
import UsersList from "./components/UsersList";
import useFormikForm from "./hooks/useSearchForm";

export interface AppContextInterface {
  mobileFiltersOpen: boolean;
  setMobileFiltersOpen: React.Dispatch<React.SetStateAction<boolean>>;
  search: string;
  handleSearchChange: ChangeEventHandler<HTMLInputElement>;
  handleSearchSubmit: FormEventHandler<HTMLFormElement>;
}

export const AppContext = createContext<AppContextInterface>({
  mobileFiltersOpen: false,
  setMobileFiltersOpen: () => {},
  search: "",
  handleSearchChange: () => {},
  handleSearchSubmit: () => {},
});

function App() {
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState<boolean>(false);
  const [search, setSearch] = useState("");

  const { value, handleSearchChange, handleSearchSubmit } = useFormikForm({
    name: "search",
    onSubmit: setSearch,
  });

  const [firstLoad, setFirstLoad] = useState(true);
  useEffect(() => {
    setFirstLoad(false);
    if (!firstLoad) {
      const timeoutId = setTimeout(() => {
        setSearch(value);
      }, 500);
      return () => clearTimeout(timeoutId);
    }
  }, [search]);
  useGetUsers({ results: 8, search });

  return (
    <AppContext.Provider
      value={{
        mobileFiltersOpen,
        setMobileFiltersOpen,
        search: value,
        handleSearchChange,
        handleSearchSubmit,
      }}
    >
      <Layout>
        <div className={"max-w-3xl mx-auto px-6 flex flex-col lg:px-6 mt-10"}>
          {/* Filters */}
          <Filters />
          <main className={`lg:col-span-8 xl:col-span-6 xl:col-start-3`}>
            {/* Search */}
            <Search />
            {/* Users List */}
            <UsersList />
          </main>
        </div>
      </Layout>
    </AppContext.Provider>
  );
}

export default App;
