import axios from "axios";
import { useEffect } from "react";
import { useQuery } from "react-query";
import { useSetRecoilState } from "recoil";
import reposState from "../state/reposState";
import { IRepos } from "../types";

enum ESort {
  CREATED = "created",
  UPDATED = "updated",
  PUBLISHED = "published",
  FULL_NAME = "full_name",
}

enum EDirection {
  ASC = "asc",
  DESC = "desc",
}

interface IUseGetRepos {
  results?: number;
  page?: number;
  org?: string;
  sort?: ESort;
}

const useGetRepos = ({ results = 8, org = "facebook" }: IUseGetRepos = {}) => {
  const setRepos = useSetRecoilState(reposState);
  console.log(org);
  const { isLoading, error, data } = useQuery(["reposData", org, results], () =>
    axios
      .get(
        `https://api.github.com/orgs/${org}/repos?per_page=${results}?sort=${sort}`,
        {
          headers: {
            Authorization: "token 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",
          },
        }
      )
      .then((res) => res.data)
      .catch((err) => err.response)
  );
  console.log(data, error);
  useEffect(() => {
    if (isLoading) {
      setRepos((repos: IRepos) => {
        return { ...repos, loading: true, error: false };
      });
    } else {
      console.log("error", error);
      if (error) setRepos({ results: data, loading: false, error: error });
      else {
        setRepos((repos: IRepos) => ({
          ...repos,
          results: data,
          loading: false,
          error: null,
        }));
      }
    }
  }, [isLoading]);
};

export default useGetRepos;
