import Layout from "./components/Layout";
import Search from "./components/Search";
import { Fragment, useState, createContext } from "react";
import { Dialog, Disclosure, Transition } from "@headlessui/react";
import { XIcon } from "@heroicons/react/outline";
import { MinusSmIcon, PlusSmIcon } from "@heroicons/react/solid";

const subCategories = [
  { name: "Totes", href: "#" },
  { name: "Backpacks", href: "#" },
  { name: "Travel Bags", href: "#" },
  { name: "Hip Bags", href: "#" },
  { name: "Laptop Sleeves", href: "#" },
];
const filters = [
  {
    id: "color",
    name: "Color",
    options: [
      { value: "white", label: "White", checked: false },
      { value: "beige", label: "Beige", checked: false },
      { value: "blue", label: "Blue", checked: true },
      { value: "brown", label: "Brown", checked: false },
      { value: "green", label: "Green", checked: false },
      { value: "purple", label: "Purple", checked: false },
    ],
  },
  {
    id: "category",
    name: "Category",
    options: [
      { value: "new-arrivals", label: "New Arrivals", checked: false },
      { value: "sale", label: "Sale", checked: false },
      { value: "travel", label: "Travel", checked: true },
      { value: "organization", label: "Organization", checked: false },
      { value: "accessories", label: "Accessories", checked: false },
    ],
  },
  {
    id: "size",
    name: "Size",
    options: [
      { value: "2l", label: "2L", checked: false },
      { value: "6l", label: "6L", checked: false },
      { value: "12l", label: "12L", checked: false },
      { value: "18l", label: "18L", checked: false },
      { value: "20l", label: "20L", checked: false },
      { value: "40l", label: "40L", checked: true },
    ],
  },
];

export interface AppContextInterface {
  mobileFiltersOpen: boolean;
  setMobileFiltersOpen: React.Dispatch<React.SetStateAction<boolean>>;
}
export const AppContext = createContext<AppContextInterface>({
  mobileFiltersOpen: false,
  setMobileFiltersOpen: () => {},
});
function App() {
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState<boolean>(false);

  return (
    <AppContext.Provider
      value={{
        mobileFiltersOpen,
        setMobileFiltersOpen,
      }}
    >
      <Layout>
        <div className="max-w-3xl mx-auto px-6 flex flex-col lg:px-6  mt-10">
          {/* Filters */}

          <main className={`lg:col-span-8 xl:col-span-6 xl:col-start-3`}>
            {/* Search Component */}
            <Search />
            <div className="mt-4">
              {false ? (
                <span className="text-sm text-gray-500 dark:text-gray-200 bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg flex flex-1">
                  User not found
                </span>
              ) : (
                <ul className="space-y-4">
                  <li className="motion-safe:animate-fadeIn">
                    <div className="bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg">
                      <div className="flex items-center justify-between">
                        <div className="flex space-x-3 items-center">
                          <div className="flex-shrink-0">
                            {true ? (
                              <img
                                className="h-10 w-10 rounded-full"
                                src={
                                  "https://randomuser.me/api/portraits/med/men/75.jpg"
                                }
                                alt=""
                              ></img>
                            ) : (
                              <svg
                                className="bg-gray-100 text-gray-300 rounded-full overflow-hidden h-10 w-10"
                                fill="currentColor"
                                viewBox="0 0 24 24"
                              >
                                <path d="M24 20.993V24H0v-2.996A14.977 14.977 0 0112.004 15c4.904 0 9.26 2.354 11.996 5.993zM16.002 8.999a4 4 0 11-8 0 4 4 0 018 0z" />
                              </svg>
                            )}
                          </div>
                          <div className="min-w-0 flex-1">
                            <p className="text-sm font-medium text-gray-900 dark:text-white mb-1">
                              <span className="hover:underline cursor-pointer">
                                Marc Olson
                              </span>
                            </p>
                            <span className="inline-flex items-center text-sm">
                              <span className="font-medium text-orange-500">
                                marc.olson@example.com
                              </span>

                              <span className="sr-only">karmas amount</span>
                            </span>
                          </div>
                        </div>

                        <button
                          type="button"
                          className="inline-flex items-center px-4 py-2 shadow-sm text-sm font-medium rounded-3xl text-white bg-gradient-to-r from-orange-500 to-pink-500 hover:to-pink-600"
                        >
                          See more
                        </button>
                      </div>
                    </div>
                  </li>
                </ul>
              )}
            </div>
          </main>
        </div>
      </Layout>
    </AppContext.Provider>
  );
}

export default App;
