import axios from "axios";
import { useEffect } from "react";
import { useQuery } from "react-query";
import { useSetRecoilState } from "recoil";
import reposState from "../state/reposState";
import { IRepos } from "../types";

interface IUseGetRepos {
  results?: number;
  page?: number;
  org?: string;
}

const useGetRepos = ({
  results = 50,
  page = 1,
  org = {},
}: IUseGetRepos = {}) => {
  const setRepos = useSetRecoilState(reposState);
  console.log(org);
  const { isLoading, error, data } = useQuery(["reposData"], () =>
    axios
      .get(
        org
          ? `https://api.github.com/orgs/${org.login}/repos?sort`
          : `https://api.github.com/repositories`,
        {
          headers: {
            Authorization: "token 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",
          },
        }
      )
      .then((res) => res.data)
      .catch((err) => err.response)
  );
  console.log(data, error);
  useEffect(() => {
    if (isLoading) {
      setRepos((repos: IRepos) => {
        return { ...repos, loading: true, error: false };
      });
    } else {
      console.log("error", error);
      if (error) setRepos({ results: data, loading: false, error: error });
      else {
        setRepos((repos: IRepos) => ({
          ...repos,
          results: [...repos.results, ...data],
          loading: false,
          error: null,
        }));
      }
    }
  }, [isLoading]);
};

export default useGetRepos;
