/*
  This example requires Tailwind CSS v2.0+ 

  This example requires some changes to your config:

  ```
  // tailwind.config.js
  module.exports = {
    // ...
    plugins: [
      // ...
      require('@tailwindcss/forms'),
    ],
  }
  ```
*/
import { useContext, useEffect, useState } from "react";
import { CheckIcon, SelectorIcon } from "@heroicons/react/solid";
import { Combobox } from "@headlessui/react";
import useGetOrgs from "../../hooks/useGetOrgs";
import orgsState from "../../state/orgsState";
import { useRecoilValue } from "recoil";
import { AppContextInterface, IOrgs, TOrg } from "../../types";
import { AppContext } from "../../App";

function classNames(...classes: string[]) {
  return classes.filter(Boolean).join(" ");
}

export default function SearchComponent() {
  const { setMobileFiltersOpen } = useContext<AppContextInterface>(AppContext);

  const [user, setUser] = useState("");
  useGetOrgs({ user });

  const [query, setQuery] = useState("");
  const [selectedPerson, setSelectedPerson] = useState();

  useEffect(() => {
    let timeoutId = setTimeout(() => {
      setUser(query);
    }, 500);
    return () => {
      clearTimeout(timeoutId);
    };
  }, [query]);

  const orgs = useRecoilValue<IOrgs>(orgsState);

  return (
    <div className="flex items-end">
      <Combobox
        as="div"
        value={selectedPerson}
        onChange={setSelectedPerson}
        className="flex-1"
      >
        <Combobox.Label className="block text-sm font-medium text-gray-700">
          Select organization by user
        </Combobox.Label>
        <div className="relative mt-1">
          <Combobox.Input
            className="w-full rounded-md border border-gray-300 bg-white py-2 pl-3 pr-10 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-1 focus:ring-indigo-500 sm:text-sm"
            onChange={(event) => setQuery(event.target.value)}
            displayValue={(org: TOrg) => org?.login}
          />
          <Combobox.Button className="absolute inset-y-0 right-0 flex items-center rounded-r-md px-2 focus:outline-none">
            <SelectorIcon
              className="h-5 w-5 text-gray-400"
              aria-hidden="true"
            />
          </Combobox.Button>

          {orgs.results.length > 0 && (
            <Combobox.Options className="absolute z-10 mt-1 max-h-60 w-full overflow-auto rounded-md bg-white py-1 text-base shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm">
              {orgs.results.map((org: TOrg) => (
                <Combobox.Option
                  key={org.login}
                  value={org}
                  className={({ active }) =>
                    classNames(
                      "relative cursor-default select-none py-2 pl-3 pr-9",
                      active ? "bg-indigo-600 text-white" : "text-gray-900"
                    )
                  }
                >
                  {({ active, selected }) => (
                    <>
                      <div className="flex">
                        <span
                          className={classNames(
                            "truncate",
                            selected ? "font-semibold" : ""
                          )}
                        >
                          {org?.login}
                        </span>
                      </div>

                      {selected && (
                        <span
                          className={classNames(
                            "absolute inset-y-0 right-0 flex items-center pr-4",
                            active ? "text-white" : "text-indigo-600"
                          )}
                        >
                          <CheckIcon className="h-5 w-5" aria-hidden="true" />
                        </span>
                      )}
                    </>
                  )}
                </Combobox.Option>
              ))}
            </Combobox.Options>
          )}
        </div>
      </Combobox>
      <span
        onClick={() => setMobileFiltersOpen(true)}
        className="w-8 h-8 ml-5 flex justify-center items-center rounded-lg text-white bg-gradient-to-r from-orange-500 to-pink-500 hover:to-pink-600 cursor-pointer"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
          strokeWidth="2"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M12 6V4m0 2a2 2 0 100 4m0-4a2 2 0 110 4m-6 8a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4m6 6v10m6-2a2 2 0 100-4m0 4a2 2 0 110-4m0 4v2m0-6V4"
          />
        </svg>
      </span>
    </div>
  );
}
