import axios from "axios";
import React, { useContext } from "react";
import { useMutation } from "react-query";
import { useSetRecoilState } from "recoil";
import { AppContext } from "../../../App";
import favouritesState from "../../../state/favouritesState";
import { IFavourites, TFavourite } from "../../../types";

interface IFavourite {
  favourite: TFavourite | null;
}

const Favourite = ({ favourite }: IFavourite) => {
  const { values } = useContext(AppContext);
  const setFavourites = useSetRecoilState(favouritesState);

  const mutation = useMutation("remove mutation", {
    mutationFn: () =>
      axios.delete(`http://localhost:8080/api/favourites/${favourite?._id}`),
    onSuccess: (result, variables, context) => {
      console.log(result, variables, context);
      setFavourites((favourites: IFavourites) => ({
        results: favourites.results.filter((fav) => fav._id !== favourite?._id),
        loading: false,
        error: null,
      }));
    },
    onError: (error, variables, context) => {
      console.log(error, variables, context);
      setFavourites((favourites: IFavourites) => ({
        results: favourites.results,
        loading: false,
        error: error,
      }));
    },
  });
  const handleRemoveFormFavourites = (e: any) => {
    e.preventDefault();
    mutation.mutate();
  };
  return (
    <li className="flex py-6">
      <div className="flex flex-1 flex-col">
        <div>
          <div className="flex justify-between text-base font-medium text-gray-900 dark:text-gray-200">
            <h3>{favourite?.repo}</h3>
          </div>
        </div>
        <div className="flex flex-1 items-end justify-between text-sm">
          <div className="flex">
            <button
              onClick={handleRemoveFormFavourites}
              type="button"
              className="font-medium text-orange-600 hover:text-orange-500 dark:text-orange-500 dark:hover:text-orange-600"
            >
              Remove from favourites
            </button>
          </div>
        </div>
      </div>
    </li>
  );
};

export default Favourite;
