import { useContext } from "react";
import { useRecoilValue } from "recoil";
import reposState from "../../state/reposState";
import RepoCard from "../RepoCard";
import { IRepos, TRepo } from "../../types";
import { AppContext } from "../../App";
import Loader from "../Loader";
import VisibilitySensor from "react-visibility-sensor";

const ReposList = ({ setPage }: { setPage: Function }) => {
  const repos = useRecoilValue<IRepos>(reposState);
  const {
    values: { search },
  } = useContext(AppContext);

  // Filter repos by full name
  const filteredUsers = repos.results.filter((repo: RepoType) => {
    const lowerCaseSearch = search.toLowerCase();
    return repo.full_name.includes(lowerCaseSearch);
  });

  const onChangeVisibility = (visible: boolean) => {
    if (visible) {
      setPage((page: number) => page + 1);
    }
  };
  return (
    <div className="mb-5">
      {!repos.loading && repos.error ? (
        <span className="mt-4 text-sm text-red-500 dark:text-red-200 bg-white dark:bg-red-700 px-4 py-6 shadow sm:p-6 rounded-lg flex flex-1">
          It has been an error getting the repositories
        </span>
      ) : (
        <>
          {filteredUsers.length === 0 && !repos.loading && (
            <span className="mt-4 text-sm text-gray-500 dark:text-gray-200 bg-white dark:bg-gray-700 px-4 py-6 shadow sm:p-6 rounded-lg flex flex-1">
              Repository not found
            </span>
          )}
          <ul className="space-y-4">
            {filteredUsers.map((repo: RepoType) => (
              <RepoCard repo={repo} key={repo.id} />
            ))}
          </ul>

          <VisibilitySensor onChange={onChangeVisibility}>
            <div
              className="p-3"
              onClick={() => setPage((page: number) => page + 1)}
            ></div>
          </VisibilitySensor>
          {repos.loading && (
            <div className="mt-4">
              <Loader />
              <Loader />
              <Loader />
              <Loader />
              <Loader />
              <Loader />
              <Loader />
              <Loader />
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ReposList;
