import { useFormik } from "formik";
import React, { useEffect, useState } from "react";
import * as Yup from "yup";

interface UseFormikInterface {
  name: string;
  onSubmit: Function;
}

const useFormikForm = ({ name, onSubmit }: UseFormikInterface) => {
  const { handleSubmit, handleChange, values } = useFormik({
    initialValues: {
      [name]: "",
    },
    enableReinitialize: true,

    onSubmit: async (values) => {
      onSubmit(values);
    },
  });
  const handleChangeName =
    "handle" + name.charAt(0).toUpperCase() + name.slice(1) + "Change";
  const handleSubmitName =
    "handle" + name.charAt(0).toUpperCase() + name.slice(1) + "Change";
  return {
    value: values[name],
    [handleChangeName]: handleChange,
    [handleSubmitName]: handleSubmit,
  };
};

export default useFormikForm;
