import { useFormik } from "formik";
import React, { useEffect, useState } from "react";
import * as Yup from "yup";

interface UseFormikInterface {
  name: string;
  onSubmit: Function;
}

const useFormikForm = async ({ name, onSubmit }: UseFormikInterface) => {
  const { handleSubmit, handleChange, values } = await useFormik({
    initialValues: {
      [name]: "",
    },
    enableReinitialize: true,

    onSubmit: async (values) => {
      onSubmit(values);
    },
  });
  return [values[name], handleChange, handleSubmit];
};

export default useFormikForm;
