import axios from "axios";
import { useEffect } from "react";
import { useQuery } from "react-query";
import { useSetRecoilState } from "recoil";
import usersState from "../state/usersState";

interface UseGetUsersInterface {
  results?: number;
  nationalities?: string[];
  search?: string;
}

const useGetUsers = ({
  results = 50,
  nationalities = [],
  search = "",
}: UseGetUsersInterface = {}) => {
  const setUsers = useSetRecoilState(usersState);

  const { isLoading, error, data, isFetching } = useQuery(
    ["randomUsersData", results, nationalities, search],
    () =>
      axios
        .get(`https://randomuser.me/api/?results=${results}`)
        .then((res) =>
          search
            ? res.data.filter(
                (user) =>
                  user.name.first.includes(search) ||
                  user.name.last.includes(search)
              )
            : res.data
        )
        .catch((err) => err.response)
  );

  if (isLoading) {
    setUsers((users) => {
      return { ...users, loading: true, error: error };
    });
  } else {
    if (error) setUsers({ ...data, loading: false, error: error });
    else setUsers({ ...data, loading: false, error: null });
  }
};

export default useGetUsers;
