import axios from "axios";
import React, { useEffect } from "react";
import { useSetRecoilState } from "recoil";
import usersState from "../state/usersState";

interface UseGetUsersInterface {
  results?: 50;
  nationalities: string[];
}

const useGetUsers = ({ results = 50 }: UseGetUsersInterface = {}) => {
  const setUsers = useSetRecoilState(usersState);
  useEffect(() => {
    axios
      .get(`https://randomuser.me/api/?results=${results}`)
      .then((res) => {
        console.log(res);
        setUsers({ ...res.data, loading: false, error: null });
      })
      .catch((err) => {
        console.log("entra");
        console.log(err.response);
      });
  }, []);
};

export default useGetUsers;
